    useEffect(async () => {

        const categoryMap = await getCategoriesAndDocuments('categories')
        console.log(categoryMap)
    }, [])

    const value = { products }
    return (
        <ProductsContext.Provider value={value}> {children} </ProductsContext.Provider>
    )
}
